mapper.select:
SELECT vid, vfrom, vreason, vstart, vend, vtime, vtype, vstatus, vto, vcheck
FROM tblleave
WHERE vfrom = #{eid}
ORDER BY vtime ASC
LIMIT #{start}, #{count};

SELECT vid, vfrom, vreason, vstart, vend, vtime, vtype, vstatus, vto, vcheck
FROM ((tbldept INNER JOIN tblemployee ON dno = tblemployee.edept) JOIN tblleave ON vfrom = tblemployee.empno)
WHERE dno = #{did}
ORDER BY vtime ASC
LIMIT #{start}, #{count};

SELECT vid, vfrom, vreason, vstart, vend, vtime, vtype, vstatus, vto, vcheck
FROM tblleave
WHERE vto = #{tid}
ORDER BY vtime ASC
LIMIT 0, 10;
